mapper.select:
select t_camera.camera_id as cameraId, t_camera.camera_code as cameraCode, t_camera.rtp_push_addr as rtpPushAddr, t_camera.camera_type_id as cameraTypeId, t_camera.direction as direction, t_camera.vehicle_direction as vehicleDirection, t_camera.site_id as siteId, t_camera.city as city, t_site.site_name as siteName, t_camera_type.camera_type_name as cameraTypeName from t_camera, t_camera_type, t_site where t_camera.camera_type_id = t_camera_type.camera_type_id and t_camera.site_id = t_site.site_id
and t_camera.camera_type_id = #{cameraTypeId}
and t_camera.camera_code = #{cameraCode}
